  filterFunction() {
    this.clicks = 0;

    this.totalInvoiceAmt = 0.0
    this.totalCollectionReceived = 0.0
    this.totalInvoiceBaseAmt = 0.0
    this.totalSstAmt = 0.0
    this.totalOutstandingAmt = 0.0
    this.totalRetentionAmt = 0.0

    this.loading = true;
    this.totalInvoiceValue = 0.0

    let listPaymentMode = {}
    let tempPaymentMode = []

    this.selectedItemsPaymentMode.forEach((x, i) => {
      const num = x.item_id
      listPaymentMode[`${num}`] = x.item_id;
      tempPaymentMode.push(listPaymentMode[listPaymentMode[`${num}`]])
    })

    if (tempPaymentMode.length == 0) {
      tempPaymentMode = ['Credit Note', 'Debit Note']
    }

    this.pdfPaymentMode = tempPaymentMode

    if (this.transactionDateFrom != "") {
      this.reportService.fetchAllPaymentHistoryFilterWithInvoiceDate(this.transactionRefNoSearch, this.paymentRefNoSearch, tempPaymentMode, this.transactionDateFrom, this.transactionDateTo).subscribe((x) => {
        this.store = [];
        this.invoicePaymentHistories = [];
        this.storing = x
        this.save = this.storing.length

        this.dtElement.dtInstance.then((dtInstance: DataTables.Api) => {
          dtInstance.destroy();
        });
        x.forEach((element, i) => {
          if (element.paymentMode == "Credit Note" || element.paymentMode == "Debit Note") {
            element.paymentDateDisplay = dateFormat(element.paymentDate, 'dd-mm-yyyy')


            this.invoicePaymentHistories.push(element)
            this.store.push(element)

            this.totalInvoiceAmt += element.totalInvoiceValue;
            this.totalCollectionReceived += element.paymentReceived;
            this.totalInvoiceBaseAmt += element.invoiceBaseValue;
            this.totalSstAmt += element.sst;
            this.totalOutstandingAmt += element.outstandingAmount;
            this.totalRetentionAmt += element.retentionAmount;
            this.transac += element.transactionRefNo
            this.pm += element.paymentMode

          }

        })


        this.loading = false;
        this.dtTrigger.next();
      });
    }
    if (this.transactionDateFrom == "") {
      this.totalInvoiceAmt = 0.0
      this.totalCollectionReceived = 0.0
      this.totalInvoiceBaseAmt = 0.0
      this.totalSstAmt = 0.0
      this.totalOutstandingAmt = 0.0
      this.totalRetentionAmt = 0.0
      this.reportService.fetchAllPaymentHistoryFilterWithoutInvoiceDate(this.transactionRefNoSearch, this.paymentRefNoSearch, tempPaymentMode).subscribe((x) => {
        this.invoicePaymentHistories = [];
        this.store = [];
        this.storing = x
        this.save = this.storing.length
        this.dtElement.dtInstance.then((dtInstance: DataTables.Api) => {
          dtInstance.destroy();
        });

        x.forEach((element, i) => {
          if (element.paymentMode == "Credit Note" || element.paymentMode == "Debit Note") {

            element.paymentDateDisplay = dateFormat(element.paymentDate, 'dd-mm-yyyy')
            element.uniqueKey = i + 1
            this.invoicePaymentHistories.push(element)
            this.store.push(element)

            this.totalInvoiceAmt += element.totalInvoiceValue;
            this.totalCollectionReceived += element.paymentReceived;
            this.totalInvoiceBaseAmt += element.invoiceBaseValue;
            this.totalSstAmt += element.sst;
            this.totalOutstandingAmt += element.outstandingAmount;
            this.totalRetentionAmt += element.retentionAmount;
            this.transac += element.transactionRefNo
            this.pm += element.paymentMode

          }
        })
        //this.store = this.invoicePaymentHistories


        // 
        this.loading = false;
        this.dtTrigger.next();
      });

    }
    setTimeout(() => {
      if (this.save == 0) {
        this.totalInvoiceAmt = 0.0
        this.totalCollectionReceived = 0.0
        this.totalInvoiceBaseAmt = 0.0
        this.totalSstAmt = 0.0
        this.totalOutstandingAmt = 0.0
        this.totalRetentionAmt = 0.0



      }
    }, 300)


  }

