 // current Date fetcher
 curDatePicker() {

  let date_ob = new Date();
  date_ob.setDate(0);
  let date = ("0" + date_ob.getDate()).slice(-2)
  let month = ("0" + [(date_ob.getMonth() + 1)]).slice(-2);
  let year = date_ob.getFullYear();

  this.curDate = year + "-" + month + "-" + date
}

  transactionDateFromHandler() {
    var date = new Date();
    var day = date.getDate();
    var month = date.getMonth() + 1;
    var year = date.getFullYear();
    var monthStr;
    var dateStr;

    if (month < 10) { monthStr = "0" + month }
    else { monthStr = month }
    (day < 10 ? dateStr = "0" + day : dateStr = day);
    this.transactionDateTo = year + "-" + monthStr + "-" + dateStr;
  }
  getInvoiceByInvoiceNo(invoiceNo, refNo, id, paymentRefNo, paymentReceived, invoiceTypeId, paymentDate, sst, invBase, paymentMode) {


    this.loadingModal = true;
    this.refNo = refNo
    this.paymentRefNo = paymentRefNo
    this.invoiceTypeId = invoiceTypeId
    this.collectionReceived = paymentReceived
    this.paymentDate = paymentDate
    this.sst = sst
    this.invBase = invBase
    this.paymentHistoryId = id

    if (paymentMode == "Credit Note") {
      this.cndnMode = "CREDIT NOTE"
      this.cndn = "CN"
      this.cndnFooter = "credit"
    }
    else {
      this.cndnMode = "DEBIT NOTE"
      this.cndn = "DN"
      this.cndnFooter = "debit"
    }

    this.reportService.fetchAllInvoiceByIdPaymentHistory(id).subscribe((x) => {

      this.invoiceNoFin09No = x.fin09InvoiceNo
      this.paymentHistorySearch = x


    })

    this.reportService.fetchAllInvoiceByCnDnId(id).subscribe((x) => {
      this.invoice = x;

      // this.invoiceNoFin09No=this.invoice.invoiceNo
      this.netAmount = this.invoice.totalInvoiceValue
      this.docRef = this.invoice.invoiceNo
      //this.districtName =this.invoice.districtName

      if (this.invoice.clinicTypeCode = '1') {
        this.clinicCode = 'PKD'
      }
      else if (this.invoice.clinicTypeCode = '2') {
        this.clinicCode = 'PPD'
      }
      this.districtName = this.clinicCode + '/' + this.invoice.districtName

      this.lastDate = this.invoice.invoiceDate
      this.districtId = this.invoice.districtId



      this.invoice.invoiceDateDisplay = dateFormat(this.invoice.invoiceDate, 'dd-mm-yyyy')
      this.invoice.invoicePaymentHistory.forEach(e => {
        e.paymentDateDisplay = dateFormat(e.paymentDate, 'dd-mm-yyyy')
      })
      this.loadingModal = false;
    })

    setTimeout(() => {
      this.reportService.fetchDistrictById(this.districtId).subscribe(x => {

        this.assign = x
        if (this.invoice.clinicTypeCode = '1') {
          this.address = this.assign.officePkdAddress
        }
        else if (this.invoice.clinicTypeCode = '2') {
          this.address = this.assign.officePpdAddress
        }
      })
    }, 700)

    // setTimeout(() => {

    // }, 1000)

  }



